import React, { useState, useEffect } from "react";
import axios from "axios";
import "./AdminDashboard.css"; // Add CSS for enhanced layout

const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const token = localStorage.getItem("token");

                if (!token) {
                    setError("Authentication token missing. Please log in again.");
                    setLoading(false);
                    return;
                }

                const response = await axios.get("http://localhost:5000/api/admin/orders", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                setOrders(response.data.orders || []);
                setError(null);
            } catch (err) {
                console.error("Error fetching orders:", err.message);
                setError("Failed to fetch orders. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    const updateOrderStatus = async (id, status) => {
        try {
            const token = localStorage.getItem("token");

            if (!token) {
                alert("Authentication token missing. Please log in again.");
                return;
            }

            await axios.put(
                `http://localhost:5000/api/admin/orders/${id}/status`,
                { status },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            alert("Order status updated successfully!");
            setOrders((prevOrders) =>
                prevOrders.map((order) =>
                    order._id === id ? { ...order, status } : order
                )
            );
        } catch (err) {
            console.error("Error updating order status:", err.message);
            alert("Failed to update order status. Please try again.");
        }
    };

    if (loading) return <p>Loading orders...</p>;
    if (error) return <p style={{ color: "red" }}>{error}</p>;

    const orderStatuses = [
        "Ordered Successfully",
        "Picked Up",
        "Processing",
        "Out for Delivery",
        "Delivered",
    ];

    return (
        <div className="admin-dashboard-container">
            <h1>Admin Dashboard</h1>
            {orders.length === 0 ? (
                <p>No orders found.</p>
            ) : (
                <div className="orders-grid">
                    {orders.map((order) => (
                        <div key={order._id} className="order-card">
                            <h3>Order ID: {order._id}</h3>
                            <p>
                                <strong>User:</strong> {order.user?.name || "N/A"} <br />
                                <small>{order.user?.email || "N/A"}</small>
                            </p>
                            <p>
                                <strong>Total Price:</strong> ${order.totalPrice.toFixed(2)}
                            </p>
                            <p>
                                <strong>Order Date:</strong>{" "}
                                {new Date(order.createdAt).toLocaleDateString()}
                            </p>

                            {/* Display detailed items */}
                            <h4>Items Ordered:</h4>
                            <ul className="products-list">
                                {order.products.map((product, index) => (
                                    <li key={index}>
                                        <strong>{product.name}</strong> <br />
                                        Price per piece: ${product.price.toFixed(2)} <br />
                                        Quantity: {product.quantity} <br />
                                        Subtotal: ${(product.price * product.quantity).toFixed(2)}
                                    </li>
                                ))}
                            </ul>

                            <p>
                                <strong>Status:</strong>
                                <select
                                    value={order.status}
                                    onChange={(e) => updateOrderStatus(order._id, e.target.value)}
                                    className="status-picker"
                                >
                                    {orderStatuses.map((status, index) => (
                                        <option key={index} value={status}>
                                            {status}
                                        </option>
                                    ))}
                                </select>
                            </p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default AdminDashboard;
