import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);
    const [loading, setLoading] = useState(true); // To handle loading state
    const [error, setError] = useState(null); // To handle errors

    // Fetch orders from the backend
    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const token = localStorage.getItem("token");

                if (!token) {
                    setError("Authentication token missing. Please log in again.");
                    setLoading(false);
                    return;
                }

                const response = await axios.get("http://localhost:5000/api/admin/orders", {
                    headers: { Authorization: `Bearer ${token}` },
                });

                console.log("Fetched orders:", response.data); // Debug response
                setOrders(response.data.orders || []); // Ensure orders are handled correctly
                setError(null); // Clear any previous errors
            } catch (err) {
                console.error("Error fetching orders:", err);
                setError("Failed to fetch orders. Please try again.");
            } finally {
                setLoading(false);
            }
        };

        fetchOrders();
    }, []);

    // Update order status
    const updateOrderStatus = async (id, status) => {
        try {
            const token = localStorage.getItem("token");

            if (!token) {
                alert("Authentication token missing. Please log in again.");
                return;
            }

            const response = await axios.put(
                `http://localhost:5000/api/admin/orders/${id}/status`,
                { status },
                { headers: { Authorization: `Bearer ${token}` } }
            );

            console.log("Order status updated:", response.data); // Debug response
            alert("Order status updated successfully!");

            // Update the order in the UI
            setOrders((prevOrders) =>
                prevOrders.map((order) =>
                    order._id === id ? { ...order, status } : order
                )
            );
        } catch (err) {
            console.error("Error updating order status:", err);
            alert("Failed to update order status. Please try again.");
        }
    };

    if (loading) {
        return <p>Loading orders...</p>;
    }

    if (error) {
        return <p style={{ color: "red" }}>{error}</p>;
    }

    return (
        <div>
            <h1>Admin Dashboard</h1>
            {orders.length === 0 ? (
                <p>No orders found.</p>
            ) : (
                <ul>
                    {orders.map((order) => (
                        <li key={order._id}>
                            <p>
                                <strong>Order ID:</strong> {order._id} <br />
                                <strong>Status:</strong> {order.status} <br />
                                <strong>Total:</strong> ${order.totalPrice.toFixed(2)} <br />
                                <strong>User:</strong> {order.user?.name || "N/A"} ({order.user?.email || "N/A"})
                            </p>
                            <button onClick={() => updateOrderStatus(order._id, "Processing")}>
                                Mark as Processing
                            </button>
                            <button onClick={() => updateOrderStatus(order._id, "Delivered")}>
                                Mark as Delivered
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AdminDashboard;
