import React, { useState, useEffect } from "react";
import axios from "axios";

const AdminDashboard = () => {
    const [orders, setOrders] = useState([]);

    useEffect(() => {
        const fetchOrders = async () => {
            try {
                const token = localStorage.getItem("token");
                const { data } = await axios.get("/api/admin/orders", {
                    headers: { Authorization: `Bearer ${token}` },
                });
                setOrders(data.orders);
            } catch (err) {
                alert("Failed to fetch admin orders.");
            }
        };

        fetchOrders();
    }, []);

    const updateOrderStatus = async (id, status) => {
        try {
            const token = localStorage.getItem("token");
            await axios.put(
                `/api/admin/orders/${id}/status`,
                { status },
                { headers: { Authorization: `Bearer ${token}` } }
            );
            alert("Order status updated successfully!");
            setOrders((prevOrders) =>
                prevOrders.map((order) =>
                    order._id === id ? { ...order, status } : order
                )
            );
        } catch (err) {
            alert("Failed to update order status.");
        }
    };

    return (
        <div>
            <h1>Admin Dashboard</h1>
            {orders.length === 0 ? (
                <p>No orders found.</p>
            ) : (
                <ul>
                    {orders.map((order) => (
                        <li key={order._id}>
                            Order ID: {order._id} - Status: {order.status} - Total: ${order.totalPrice}
                            <button onClick={() => updateOrderStatus(order._id, "Processing")}>
                                Mark as Processing
                            </button>
                            <button onClick={() => updateOrderStatus(order._id, "Delivered")}>
                                Mark as Delivered
                            </button>
                        </li>
                    ))}
                </ul>
            )}
        </div>
    );
};

export default AdminDashboard;
