import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./Checkout.css";

const Checkout = () => {
    const navigate = useNavigate();
    const [orderDetails, setOrderDetails] = useState({
        items: [], // Initialize with empty array
        total: 0,
    });

    // Simulate fetching cart items (Replace with actual API call or state management)
    useEffect(() => {
        const isLoggedIn = localStorage.getItem("user");
        if (!isLoggedIn) {
            alert("You must register or log in to proceed!");
            navigate("/register");
        } else {
            // Mock order details (Replace with actual data fetching logic)
            const cartItems = JSON.parse(localStorage.getItem("cart")) || [];
            const totalCost = cartItems.reduce((total, item) => total + item.price * item.quantity, 0);

            setOrderDetails({
                items: cartItems,
                total: totalCost,
            });
        }
    }, [navigate]);

    const handlePayment = () => {
        alert("Payment Successful!"); // Integrate LINE Pay API here
    };

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>
            <div className="order-summary">
                <h2>Order Summary</h2>
                {orderDetails.items.length > 0 ? (
                    <ul>
                        {orderDetails.items.map((item, index) => (
                            <li key={index}>
                                <span>{item.name}</span>
                                <span>Qty: {item.quantity}</span>
                                <span>Price: ${item.price.toFixed(2)}</span>
                            </li>
                        ))}
                    </ul>
                ) : (
                    <p>No items in the cart.</p>
                )}
                <h3>Total: ${orderDetails.total.toFixed(2)}</h3>
            </div>
            <button className="pay-button" onClick={handlePayment}>
                Pay Now
            </button>
        </div>
    );
};

export default Checkout;
