import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";
import "./TrackOrder.css";

const TrackOrder = () => {
    const { id } = useParams();
    const [order, setOrder] = useState(null);

    const statusStages = [
        "Pending",
        "Processing",
        "Shipped",
        "Delivered",
    ];

    useEffect(() => {
        const fetchOrder = async () => {
            try {
                console.log("Fetching order for ID:", id); // Log the ID
                const token = localStorage.getItem("token");
    
                if (!token) {
                    console.error("Token is missing.");
                    setError("Authentication token missing. Please log in.");
                    return;
                }
    
                const { data } = await axios.get(`http://localhost:5000/api/orders/${id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
    
                console.log("Fetched Order Data:", data); // Log API response
                setOrder(data.order);
            } catch (err) {
                console.error("Error fetching order details:", err.message); // Log error
                setError("Failed to fetch order details. Please try again.");
            }
        };
    
        fetchOrder();
    }, [id]);
    

    if (!order) return <p>Loading...</p>;

    const currentStageIndex = statusStages.indexOf(order.status);

    return (
        <div className="track-order-container">
            <h1>Track Your Order</h1>
            <div className="order-details">
                <p><strong>Order ID:</strong> {order._id}</p>
                <p><strong>Status:</strong> {order.status}</p>
                <p><strong>Total Price:</strong> ${order.totalPrice}</p>
                <p><strong>Products:</strong></p>
                <ul>
                    {order.products.map((product) => (
                        <li key={product.productId._id}>
                            {product.productId.name} - Qty: {product.quantity}
                        </li>
                    ))}
                </ul>
            </div>

            {/* Status Progress Bar */}
            <div className="progress-bar">
                {statusStages.map((stage, index) => (
                    <div
                        key={index}
                        className={`progress-step ${index <= currentStageIndex ? "active" : ""}`}
                    >
                        <div className="step-circle">{index + 1}</div>
                        <p className="step-label">{stage}</p>
                    </div>
                ))}
            </div>
        </div>
    );
};

export default TrackOrder;
